import { useState, useEffect } from "react";
import { Routes, Route, Navigate, Link } from "react-router-dom";
import axios from "axios";
import './styles.css';

function App() {
  const [cryptos, setCryptos] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    axios.get("https://api.coingecko.com/api/v3/coins/markets", {
      params: { vs_currency: "usd" }
    }).then(response => {
      setCryptos(response.data);
    });
  }, []);

  const handleFavorite = (crypto) => {
    if (!user) {
      return window.location.href = "/login";
    }
    setFavorites([...favorites, crypto]);
  };

  return (
    <>
    <nav className="navbar">
      <Link to="/">Inicio</Link>
      {user && <Link to="/favorites">Favoritos</Link>}
      {user ? <button onClick={() => setUser(null)}>Cerrar Sesión</button> : <Link to="/login">Iniciar Sesión</Link>}
    </nav>
    <Routes>
      <Route path="/" element={<Home cryptos={cryptos} handleFavorite={handleFavorite} />} />
      <Route path="/favorites" element={user ? <Favorites favorites={favorites} /> : <Navigate to="/login" />} />
      <Route path="/login" element={<Login setUser={setUser} />} />
    </Routes>
  </>
);
}

function Home({ cryptos, handleFavorite }) {
  return (
    <div className="container">
      <h1>Criptomonedas</h1>
      <ul>
        {cryptos.map(crypto => (
          <li key={crypto.id} className="crypto-card">
            <span>{crypto.name} - ${crypto.current_price}</span>
            <button onClick={() => handleFavorite(crypto)}>Agregar a Favoritos</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

function Favorites({ favorites }) {
  return (
    <div className="container">
      <h1>Favoritos</h1>
      <ul>
        {favorites.map(crypto => (
          <li key={crypto.id} className="crypto-card">
            <span>{crypto.name} - ${crypto.current_price}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

function Login({ setUser }) {
  const handleLogin = () => {
    setUser("user123");
    window.location.href = "/";
  };
  return (
    <div className="container">
      <h1>Iniciar Sesión</h1>
      <button onClick={handleLogin}>Ingresar</button>
    </div>
  );
}

export default App;
