import { useState, useEffect } from "react";
import { Routes, Route, Navigate, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./styles.css";

function App() {
  const [cryptos, setCryptos] = useState([]);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("currentUser")) || null);
  const [favorites, setFavorites] = useState(
    user ? JSON.parse(localStorage.getItem(`favorites_${user.username}`)) || [] : []
  );

  useEffect(() => {
    axios
      .get("https://api.coingecko.com/api/v3/coins/markets", {
        params: { vs_currency: "usd" },
      })
      .then((response) => {
        setCryptos(response.data);
      });
  }, []);

  useEffect(() => {
    if (user) {
      const savedFavorites = JSON.parse(localStorage.getItem(`favorites_${user.username}`)) || [];
      setFavorites(savedFavorites);
    } else {
      setFavorites([]); // Limpiar favoritos si no hay usuario
    }
  }, [user]);

  const handleFavorite = (crypto, navigate) => {
    if (!user) {
      navigate("/login");
      return;
    }

    const alreadyFavorite = favorites.some((fav) => fav.id === crypto.id);
    if (!alreadyFavorite) {
      const updatedFavorites = [...favorites, crypto];
      setFavorites(updatedFavorites);
    }

    navigate("/favorites");
  };

  const handleLogout = () => {
    setUser(null);
    setFavorites([]);
    localStorage.removeItem("currentUser");
  };

  return (
    <>
      <nav className="navbar">
        <Link to="/">Inicio</Link>
        {user && <Link to="/favorites">Favoritos</Link>}
        {user ? (
          <button onClick={handleLogout}>Cerrar Sesión</button>
        ) : (
          <>
            <Link to="/login">Iniciar Sesión</Link>
            <Link to="/register">Registrar</Link>
          </>
        )}
      </nav>

      <Routes>
        <Route path="/" element={<Home cryptos={cryptos} handleFavorite={handleFavorite} />} />
        <Route path="/favorites" element={user ? <Favorites favorites={favorites} /> : <Navigate to="/login" />} />
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/register" element={<Register setUser={setUser} />} />
      </Routes>
    </>
  );
}

function Home({ cryptos, handleFavorite }) {
  const navigate = useNavigate();

  return (
    <div className="container">
      <h1>Criptomonedas</h1>
      <ul>
        {cryptos.map((crypto) => (
          <li key={crypto.id} className="crypto-card">
            <span>{crypto.name} - ${crypto.current_price}</span>
            <button onClick={() => handleFavorite(crypto, navigate)}>Agregar a Favoritos</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

function Favorites({ favorites }) {
  return (
    <div className="container">
      <h1>Favoritos</h1>
      {favorites.length === 0 ? (
        <p>No tienes criptomonedas en favoritos.</p>
      ) : (
        <ul>
          {favorites.map((crypto) => (
            <li key={crypto.id} className="crypto-card">
              <span>{crypto.name} - ${crypto.current_price}</span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

function Login({ setUser }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    const users = JSON.parse(localStorage.getItem("users")) || [];
    const foundUser = users.find((u) => u.username === username && u.password === password);

    if (foundUser) {
      setUser(foundUser);
      localStorage.setItem("currentUser", JSON.stringify(foundUser));
      navigate("/");
    } else {
      alert("Usuario o contraseña incorrectos");
    }
  };

  return (
    <div className="container">
      <h1>Iniciar Sesión</h1>
      <input type="text" placeholder="Usuario" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Contraseña" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Ingresar</button>
    </div>
  );
}

function Register({ setUser }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleRegister = () => {
    if (!username || !email || !password) {
      alert("Por favor, complete todos los campos");
      return;
    }

    const users = JSON.parse(localStorage.getItem("users")) || [];
    const userExists = users.some((u) => u.username === username);

    if (userExists) {
      alert("El usuario ya existe");
      return;
    }

    const newUser = { username, email, password };
    users.push(newUser);
    localStorage.setItem("users", JSON.stringify(users));

    setUser(newUser);
    localStorage.setItem("currentUser", JSON.stringify(newUser));

    navigate("/");
  };

  return (
    <div className="container">
      <h1>Registro</h1>
      <input type="text" placeholder="Usuario" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Contraseña" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleRegister}>Registrarse</button>
    </div>
  );
}

export default App;
