import { useState, useEffect } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import "chart.js/auto";
import "./styles.css";

function App() {
  const [cryptos, setCryptos] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [user, setUser] = useState(null);
  const [history, setHistory] = useState([]);  // Estado para el historial de precios
  const navigate = useNavigate();

  // Función para obtener el historial de precios de una criptomoneda
  const fetchCryptoHistory = async (cryptoId) => {
    try {
      const response = await axios.get(`https://coingecko.p.rapidapi.com/exchanges/%7Bid%7D`, {
        params: { vs_currency: "usd", days: 3 },
        headers: {
          "X-RapidAPI-Key": "718fc6ca61msh717b4021c500422p1228d4js", // Reemplaza esto con tu clave de API
          "X-RapidAPI-Host": "coingecko.p.rapidapi.com",
        },
      });
      return response.data.prices; // Devuelve los precios
    } catch (error) {
      console.error("Error al obtener historial de precios:", error.message);
      return [];  // Devuelve un array vacío si ocurre un error
    }
  };

  // Llamar la API de CoinGecko para obtener las criptos
  useEffect(() => {
    const fetchCryptos = async () => {
      try {
        const response = await axios.get("https://api.coingecko.com/api/v3/coins/markets", {
          params: { vs_currency: "usd" },
        });
        setCryptos(response.data);
      } catch (error) {
        console.error("Error al obtener criptomonedas:", error.message);
        alert("Hubo un problema al cargar las criptomonedas. Inténtalo más tarde.");
      }
    };

    fetchCryptos();
  }, []);

  const handleFavorite = (crypto) => {
    if (!user) {
      navigate("/login");
      return;
    }
    if (!favorites.some((fav) => fav.id === crypto.id)) {
      const newFavorites = [...favorites, crypto];
      setFavorites(newFavorites);
    }
    navigate("/favorites");
  };

  const removeFavorite = (cryptoId) => {
    const updatedFavorites = favorites.filter((crypto) => crypto.id !== cryptoId);
    setFavorites(updatedFavorites);
  };

  const handleLogout = () => {
    setUser(null);
    setFavorites([]);
    localStorage.removeItem("user");
  };

  return (
    <>
      <nav className="navbar">
        <Link to="/">Inicio</Link>
        {user && <Link to="/favorites">Favoritos</Link>}
        {user ? (
          <button onClick={handleLogout}>Cerrar Sesión</button>
        ) : (
          <>
            <Link to="/login">Iniciar Sesión</Link>
            <Link to="/register">Registrar</Link>
          </>
        )}
      </nav>
      <Routes>
        <Route
          path="/"
          element={<Home cryptos={cryptos} handleFavorite={handleFavorite} fetchCryptoHistory={fetchCryptoHistory} />}
        />
        <Route
          path="/favorites"
          element={user ? <Favorites favorites={favorites} removeFavorite={removeFavorite} /> : <Navigate to="/login" />}
        />
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/register" element={<Register setUser={setUser} />} />
      </Routes>
    </>
  );
}

// Componente para mostrar el gráfico de precios de una criptomoneda
function CryptoHistoryPopup({ cryptoId, fetchCryptoHistory }) {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const fetchHistory = async () => {
      const data = await fetchCryptoHistory(cryptoId);
      if (data.length > 0) {
        setHistory(data);
      } else {
        setHistory([]);
      }
    };

    fetchHistory();
  }, [cryptoId, fetchCryptoHistory]);

  const data = {
    labels: history.map((entry) => new Date(entry[0]).toLocaleDateString()),
    datasets: [
      {
        label: "Precio en USD",
        data: history.map((entry) => entry[1]),
        borderColor: "#00f",
        fill: false,
      },
    ],
  };

  return (
    <div className="popup">
      <Line data={data} />
    </div>
  );
}

// Componente principal con las criptomonedas
function Home({ cryptos, handleFavorite, fetchCryptoHistory }) {
  const [hoveredCrypto, setHoveredCrypto] = useState(null);

  return (
    <div className="container">
      <h1>Criptomonedas</h1>
      <div className="crypto-grid">
        {cryptos.map((crypto) => (
          <div key={crypto.id} className="crypto-card">
            <div
              className="crypto-name"
              onMouseEnter={() => setHoveredCrypto(crypto.id)}
              onMouseLeave={() => setHoveredCrypto(null)}
            >
              <span>{crypto.name} - ${crypto.current_price}</span>
            </div>
            <button onClick={() => handleFavorite(crypto)}>Agregar a Favoritos</button>
            {hoveredCrypto === crypto.id && <CryptoHistoryPopup cryptoId={crypto.id} fetchCryptoHistory={fetchCryptoHistory} />}
          </div>
        ))}
      </div>
    </div>
  );
}

function Favorites({ favorites, removeFavorite }) {
  const sortedFavorites = [...favorites].sort((a, b) => b.current_price - a.current_price);

  return (
    <div className="container">
      <h1>Favoritos</h1>
      {sortedFavorites.length > 0 ? (
        <ul>
          {sortedFavorites.map((crypto) => (
            <li key={crypto.id} className="crypto-card">
              <span>{crypto.name} - ${crypto.current_price}</span>
              <button onClick={() => removeFavorite(crypto.id)}>Eliminar</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No tienes criptomonedas en favoritos.</p>
      )}
    </div>
  );
}

function Login({ setUser }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    const storedUser = JSON.parse(localStorage.getItem(`user_${username}`));

    if (storedUser && storedUser.password === password) {
      setUser(storedUser);
      localStorage.setItem("user", JSON.stringify(storedUser));
      navigate("/");
    } else {
      alert("Usuario o contraseña incorrectos");
    }
  };

  return (
    <div className="container">
      <h1>Iniciar Sesión</h1>
      <input type="text" placeholder="Usuario" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Contraseña" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Ingresar</button>
    </div>
  );
}

function Register({ setUser }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleRegister = () => {
    if (username && email && password) {
      const newUser = { username, email, password };
      localStorage.setItem(`user_${username}`, JSON.stringify(newUser));
      setUser(newUser);
      localStorage.setItem("user", JSON.stringify(newUser));
      navigate("/");
    } else {
      alert("Por favor, complete todos los campos");
    }
  };

  return (
    <div className="container">
      <h1>Registro</h1>
      <input type="text" placeholder="Usuario" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Contraseña" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleRegister}>Registrarse</button>
    </div>
  );
}

export default App;
