import { useState, useEffect } from "react";
import { Routes, Route, Navigate, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./styles.css";

function App() {
  const [cryptos, setCryptos] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("https://api.coingecko.com/api/v3/coins/markets", {
        params: { vs_currency: "usd" },
      })
      .then((response) => {
        setCryptos(response.data);
      });
  }, []);

  // Cargar usuario y favoritos al montar la app
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUser(storedUser);
      const savedFavorites = JSON.parse(localStorage.getItem(`favorites_${storedUser.username}`)) || [];
      setFavorites(savedFavorites);
    }
  }, []);

  // Guardar favoritos en localStorage cuando cambian
  useEffect(() => {
    if (user) {
      localStorage.setItem(`favorites_${user.username}`, JSON.stringify(favorites));
    }
  }, [favorites, user]);

  const handleFavorite = (crypto) => {
    if (!user) {
      navigate("/login");
      return;
    }
    const newFavorites = [...favorites, crypto];
    setFavorites(newFavorites);
    localStorage.setItem(`favorites_${user.username}`, JSON.stringify(newFavorites));
    navigate("/favorites");
  };

  const handleLogout = () => {
    setUser(null);
    setFavorites([]); // Limpiar favoritos al cerrar sesión
    localStorage.removeItem("user");
  };

  return (
    <>
      <nav className="navbar">
        <Link to="/">Inicio</Link>
        {user && <Link to="/favorites">Favoritos</Link>}
        {user ? (
          <button onClick={handleLogout}>Cerrar Sesión</button>
        ) : (
          <>
            <Link to="/login">Iniciar Sesión</Link>
            <Link to="/register">Registrar</Link>
          </>
        )}
      </nav>
      <Routes>
        <Route path="/" element={<Home cryptos={cryptos} handleFavorite={handleFavorite} />} />
        <Route path="/favorites" element={user ? <Favorites favorites={favorites} /> : <Navigate to="/login" />} />
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/register" element={<Register setUser={setUser} />} />
      </Routes>
    </>
  );
}

function Home({ cryptos, handleFavorite }) {
  return (
    <div className="container">
      <h1>Criptomonedas</h1>
      <ul>
        {cryptos.map((crypto) => (
          <li key={crypto.id} className="crypto-card">
            <span>{crypto.name} - ${crypto.current_price}</span>
            <button onClick={() => handleFavorite(crypto)}>Agregar a Favoritos</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

function Favorites({ favorites }) {
  return (
    <div className="container">
      <h1>Favoritos</h1>
      <ul>
        {favorites.map((crypto) => (
          <li key={crypto.id} className="crypto-card">
            <span>{crypto.name} - ${crypto.current_price}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

function Login({ setUser }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    const storedUser = JSON.parse(localStorage.getItem(`user_${username}`));

    if (storedUser && storedUser.password === password) {
      setUser(storedUser);
      localStorage.setItem("user", JSON.stringify(storedUser));
      navigate("/");
    } else {
      alert("Usuario o contraseña incorrectos");
    }
  };

  return (
    <div className="container">
      <h1>Iniciar Sesión</h1>
      <input type="text" placeholder="Usuario" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Contraseña" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Ingresar</button>
    </div>
  );
}

function Register({ setUser }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleRegister = () => {
    if (username && email && password) {
      const newUser = { username, email, password };
      localStorage.setItem(`user_${username}`, JSON.stringify(newUser));
      setUser(newUser);
      localStorage.setItem("user", JSON.stringify(newUser));
      navigate("/");
    } else {
      alert("Por favor, complete todos los campos");
    }
  };

  return (
    <div className="container">
      <h1>Registro</h1>
      <input type="text" placeholder="Usuario" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Contraseña" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleRegister}>Registrarse</button>
    </div>
  );
}

export default App;
