import { useState, useEffect } from "react";
import { Routes, Route, Navigate, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./styles.css";
import CryptoHistoryPopup from "./CryptoHistoryPopup";

function App() {
  const [cryptos, setCryptos] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("https://api.coingecko.com/api/v3/coins/markets", {
        params: { vs_currency: "usd" },
      })
      .then((response) => {
        setCryptos(response.data);
      });
  }, []);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUser(storedUser);
      loadFavorites(storedUser.username);
    }
  }, []);

  useEffect(() => {
    if (user) {
      saveFavorites(user.username, favorites);
    }
  }, [favorites, user]);

  const loadFavorites = (username) => {
    const savedFavorites = JSON.parse(localStorage.getItem(`favorites_${username}`)) || [];
    setFavorites(savedFavorites);
  };

  const saveFavorites = (username, favorites) => {
    localStorage.setItem(`favorites_${username}`, JSON.stringify(favorites));
  };

  return (
    <>
      <nav className="navbar">
        <Link to="/">Inicio</Link>
        {user && <Link to="/favorites">Favoritos</Link>}
        {user ? (
          <button onClick={() => {
            setUser(null);
            setFavorites([]);
            localStorage.removeItem("user");
          }}>Cerrar Sesión</button>
        ) : (
          <>
            <Link to="/login">Iniciar Sesión</Link>
            <Link to="/register">Registrar</Link>
          </>
        )}
      </nav>
      <Routes>
        <Route path="/" element={<Home cryptos={cryptos} />} />
        <Route path="/favorites" element={user ? <Favorites favorites={favorites} /> : <Navigate to="/login" />} />
      </Routes>
    </>
  );
}

function Home({ cryptos }) {
  return (
    <div className="container">
      <h1>Criptomonedas</h1>
      <ul>
        {cryptos.map((crypto) => (
          <li key={crypto.id} className="crypto-card">
            <CryptoHistoryPopup crypto={crypto} />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
